import React, { useState } from 'react'
import { Accordion, 
    AccordionDetails, 
    AccordionSummary, 
    Box, 
    Divider, 
    Typography, 
    Stack, 
    Select, 
    MenuItem,
    FormControl, 
    InputLabel,
    Button} from '@mui/material'
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import hitRoller from './HitRoller';
import "../UnitColumn.css"
import combatValues from "../data/factions.json"

function UnitColumn(props) {

    const [totalHits, setTotalHits] = useState(0);
    const [shipNumber, setShipNumber] = useState(0);
    const [unitLevel, setUnitLevel] = useState(1);
    const [rolls, setRolls] = useState([]);

    function changeUnitNumber(e) {
        setShipNumber(e.target.value);
    }

    function changeUnitLevel(e) {
        setUnitLevel(e.target.value);
    }

    function rollTheBones() {
        setRolls(hitRoller(shipNumber));
        setTotalHits(0);
        checkHits();
    }

    function checkHits() {
        console.log(rolls)
        rolls.map(roll => {
            if (unitLevel === 1) {
                if (roll >= combatValues[props.index].combat.Cruiser['1cv']) {
                    setTotalHits(totalHits++);
                }
            } else {
                if (roll >= combatValues[props.index].combat.Cruiser['2cv']) {
                    setTotalHits(totalHits++);
                }
            }
        })
    }

    return (
        <Stack gap={1}>
            <FormControl variant="standard">
                <InputLabel sx={{color: 'white'}}>{`Number of ${props.unit}s`}</InputLabel>
                <Select onChange={changeUnitNumber} sx={{color: 'white'}} value={shipNumber} label={`Number of ${props.unit}s`}>
                    <MenuItem value={0}>0</MenuItem>
                    <MenuItem value={1}>1</MenuItem>
                    <MenuItem value={2}>2</MenuItem>
                    <MenuItem value={3}>3</MenuItem>
                    <MenuItem value={4}>4</MenuItem>
                    <MenuItem value={5}>5</MenuItem>
                    <MenuItem value={6}>6</MenuItem>
                    <MenuItem value={7}>7</MenuItem>
                    <MenuItem value={8}>8</MenuItem>
                </Select>
            </FormControl>
            <FormControl variant="standard">
            <InputLabel sx={{color: 'white'}}>{`Level of ${props.unit}`}</InputLabel>
                <Select onChange={changeUnitLevel} sx={{color: 'white'}} value={unitLevel} label={`Level of ${props.unit}`}>
                    <MenuItem value={1}>1</MenuItem>
                    <MenuItem value={2}>2</MenuItem>
                </Select>
            </FormControl>
            <Button onClick={rollTheBones} variant='contained'>
                {`Roll ${props.unit}s`}
            </Button>
            <Box sx={{
                display: 'flex',
                flexDirection: 'column',
                minHeight: '200px',
                maxHeight: '600px',
                width: '150px',
                bgcolor: "#0B3C5D",
                padding: '5px',
                borderRadius: '4px'
            }}>
                <img src={require(`../img/ships/${props.unit}.png`)} alt="" style={{height: '50px'}}/>
                <Divider variant="middle" sx={{bgcolor: '#D9B310', mt: '10px', mb: '10px'}}/>
                <Accordion disableGutters sx={{bgcolor: 'transparent', boxShadow: 'none', marginBottom: 'auto'}}>
                    <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                        <Typography sx={{color: 'white'}}>Rolls</Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                        {
                            rolls.map(roll => {
                                return(<Typography sx={{color: 'white'}}>{roll}</Typography>);
                            })
                        }
                    </AccordionDetails>
                </Accordion>
                <Divider variant="middle" sx={{bgcolor: '#D9B310', mt: '10px', mb: '10px'}}/>
                <Box sx={{display: 'flex', flexDirection: 'column', bgcolor: '#328CC1', borderRadius: '4px'}}>
                    <Typography sx={{color: '#D9B310'}}>{totalHits}</Typography>
                    <Typography sx={{color: 'white'}}>Total Hits</Typography>
                </Box>
            </Box>
        </Stack>
  )
}

export default UnitColumn
