import React, { useEffect, useState } from 'react'
import { Accordion, 
    AccordionDetails, 
    AccordionSummary, 
    Box, 
    Divider, 
    Typography, 
    Stack, 
    Select, 
    MenuItem,
    FormControl, 
    InputLabel,
    Button,
    TextField,
    styled,
    InputBase} from '@mui/material'
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import hitRoller from './HitRoller';
import "../UnitColumn.css"
import combatValues from "../data/factions.json"
import DiceRoll from './DiceRoll';

function UnitColumn(props) {

    const [totalHits, setTotalHits] = useState(0);
    const [shipNumber, setShipNumber] = useState(1);
    const [unitLevel, setUnitLevel] = useState(1);
    const [rolls, setRolls] = useState([]);
    var numberOfShipsOptions = [];

    function changeUnitNumber(e) {
        setShipNumber(e.target.value);
    }

    function changeUnitLevel(e) {
        setUnitLevel(e.target.value);
    }

    function rollTheBones() {
        setRolls(hitRoller(shipNumber, combatValues[props.index].combat[props.unit]['shotCount']));
    }

    function checkHits() {
        
    }

    useEffect(() => {
        setTotalHits(0);
        let hitCount = 0;
        rolls.map(roll => {
            if (unitLevel === 1) {
                if (roll >= combatValues[props.index].combat[props.unit]['1cv']) {
                    hitCount++;
                }
            } else {
                if (roll >= combatValues[props.index].combat[props.unit]['2cv']) {
                    hitCount++;
                }
            }
        })
        setTotalHits(hitCount);
    }, [rolls])

    for (let i = 1; i <= props.maxUnits; i++) {
        numberOfShipsOptions.push(<MenuItem key={i} value={i}>{i}</MenuItem>);
    }

    return (
        <Stack gap={1} sx={{margin: {xs: '10px', md: '30px'}, width: '160px', overflow: 'hidden'}}>
            <FormControl variant="standard" disabled={props.maxUnits === 1 ? true : false}>
                <InputLabel shrink sx={{color: 'white'}}>{`Number of ${props.unit}s`}</InputLabel>
                { (props.maxUnits !== -1) &&
                    <Select input={<customInputBase />} onChange={changeUnitNumber} sx={{color: 'white', "&:before": {color: '#D9B310'}, "&:after": {color: '#D9B310'}}} value={shipNumber} label={`Number of ${props.unit}s`}>
                        {numberOfShipsOptions}
                    </Select>
                }
                { (props.maxUnits === -1) &&
                    <TextField type="number" sx={{ml: '-10px'}} InputLabelProps={{shrink: false}} 
                    onChange={changeUnitNumber} value={shipNumber} label=" " variant="standard" inputProps={{style: {textAlign: 'center', color: 'white'}}} />
                }
            </FormControl>
            <FormControl variant="standard" disabled={props.unit === "Flagship" || (props.unit === "Warsun" && props.index !== 3) ? true : false}>
            <InputLabel shrink sx={{color: 'white'}}>{`Level of ${props.unit}`}</InputLabel>
                <Select onChange={changeUnitLevel} sx={{color: 'white'}} value={unitLevel} label={`Level of ${props.unit}`}>
                    <MenuItem value={1}>1</MenuItem>
                    <MenuItem value={2}>2</MenuItem>
                </Select>
            </FormControl>
            <Button onClick={rollTheBones} variant='contained' sx={{fontSize: '1rem'}}>
                {`Roll`}
            </Button>
            <Box sx={{
                display: 'flex',
                flexDirection: 'column',
                minHeight: '200px',
                maxHeight: '600px',
                width: '150px',
                bgcolor: "#0B3C5D",
                padding: '5px',
                borderRadius: '4px',
            }}>
                <Typography sx={{color: 'white', fontSize: '1.1rem'}}><b>{props.unit.toUpperCase()}</b></Typography>
                <Divider variant="middle" sx={{bgcolor: '#D9B310',}}/>
                <Typography sx={{color: 'white', fontSize: '1.1rem'}}><b>ROLLS</b></Typography>
                <Box sx={{display: 'flex', marginBottom: 'auto', 
                flexWrap: 'wrap', justifyContent: 'center', width: '100%', maxHeight: '90px', overflow: 'auto',
                "&::-webkit-scrollbar": {width: "10px"},
                "&::-webkit-scrollbar-thumb": {bgcolor: '#D9B310', borderRadius: '4px', "&:active": {bgcolor: '#c9a60f'}}}}>
                    {
                        rolls.map(roll => {
                            //return(<Typography key={Math.random() * 1000000} sx={{color: 'white'}}>{roll % 10}</Typography>);
                            return(<DiceRoll key={Math.random() * 1000000} num={roll % 10}></DiceRoll>)
                        })
                    }
                </Box>
                
                {/* <Accordion disableGutters sx={{bgcolor: 'transparent', boxShadow: 'none', marginBottom: 'auto'}}>
                    <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                        <Typography sx={{color: 'white'}}>Rolls</Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                        {
                            rolls.map(roll => {
                                return(<Typography key={Math.random() * 1000000} sx={{color: 'white'}}>{roll}</Typography>);
                            })
                        }
                    </AccordionDetails>
                </Accordion> */}
                <Divider variant="middle" sx={{bgcolor: '#D9B310', mt: '10px', mb: '10px'}}/>
                <Box sx={{display: 'flex', bgcolor: '#328CC1', borderRadius: '4px', justifyContent: 'center'}}>
                    <Typography sx={{color: '#D9B310', fontSize: '24px', mr: '10px'}}>{totalHits}</Typography>
                    <Typography sx={{color: 'white', fontSize: '24px'}}>Hit{totalHits === 1 ? '' : 's'}</Typography>
                </Box>
            </Box>
        </Stack>
  )
}

export default UnitColumn
