import { Stack, Button, TextField, FormControl, InputLabel, Typography } from '@mui/material';
import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router';
import {MOContext} from "../context/ManualOverrideContext";

function UnitColumnsHeader() {

  const navigate = useNavigate();

  const {manualOverride, setManualOverride} = useContext(MOContext);

  return (
    <Stack gap={2} sx={{alignItems: 'center'}}>
        <Button variant='contained' sx={{bgcolor: '#328CC1', width: '50%'}} onClick={() => {navigate("/")}}>Home</Button>
        <FormControl variant="standard" >
            <InputLabel shrink sx={{color: 'white'}}>Manual Override</InputLabel>
            <TextField label=" " variant="standard" sx={{width: '100px'}} 
            inputProps={{style: {textAlign: 'center', color: 'white'}}} 
            type="number" InputLabelProps={{shrink: false}} onChange={(e) => setManualOverride(parseInt(e.target.value))} value={manualOverride}/>
        </FormControl>
        <Typography sx={{fontSize: '24px', color: 'white'}}>Use this to set any positive or negative modifers for a roll</Typography>
    </Stack>
  )
}

export default UnitColumnsHeader
