import { Box, Button, TextField, FormControl, InputLabel, Typography } from '@mui/material';
import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router';
import {MOContext} from "../context/ManualOverrideContext";

function UnitColumnsHeader() {

  const navigate = useNavigate();

  const {manualOverride, setManualOverride} = useContext(MOContext);

  return (
    <Box sx={{justifyContent: 'flex-start', display: 'flex', width: {xs: '82.5%', md: '50%'}}}>
        <Button variant='contained' sx={{bgcolor: '#328CC1', width: '160px', mr: 'auto'}} onClick={() => {navigate("/")}}>Home</Button>
        <FormControl variant="standard" >
            <InputLabel shrink sx={{color: 'white'}}>Roll Modifier</InputLabel>
            <TextField label=" " variant="standard" 
            inputProps={{style: {textAlign: 'center', color: 'white'}}} sx={{width: '160px'}}
            type="number" InputLabelProps={{shrink: false}} onChange={(e) => setManualOverride(parseInt(e.target.value))} value={manualOverride}/>
        </FormControl>
        <Typography><i>The displayed roll includes the modifier you set at the time of the roll. Hover/</i></Typography>
    </Box>
  )
}

export default UnitColumnsHeader
