import React, { useContext } from 'react';
import d10 from "../img/d10.png";
import d10AFB from "../img/d10AFB.png";
import { Typography, Box, Tooltip, Paper } from '@mui/material';

function DiceRoll(props) {
  if (!props.ability) 
  {
    return (
      <Tooltip title={`${props.num}+${props.or}=${props.num+props.or}`} arrow PopperProps={{
        sx: {
          "& .MuiTooltip-tooltip": {
            bgcolor: '#328CC1',
            "& .MuiTooltip-arrow": {
              color: '#328CC1'
            }
          }
        }
      }}>
        <Box sx={{position: 'relative', userSelect: 'none'}}>
            <img src={d10} style={{height: '30px', pointerEvents: 'none'}}></img>
            <Typography sx={{userSelect: 'none',color: 'black', 
            position: 'absolute', transform: "translateX(140%) translateY(-115%)"}}
            >
              {
                props.num + props.or >= 10 ? <b>{0}</b> : <b>{props.num + props.or}</b>
              }
            </Typography>
        </Box>
      </Tooltip>
    )
  } else {
    return (
      <Box sx={{position: 'relative', userSelect: 'none'}}>
          <img src={d10AFB} style={{height: '30px', pointerEvents: 'none'}}></img>
          <Typography sx={{userSelect: 'none',color: 'black', 
          position: 'absolute', transform: "translateX(140%) translateY(-115%)"}}
          >
            {
              props.num + props.or >= 10 ? <b>{0}</b> : <b>{props.num + props.or}</b>
            }
          </Typography>
      </Box>
    )
  }

}

export default DiceRoll
